CREATE TABLE PDP_PLUGINS (
    ID BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY (  
		    START WITH +1  
		    INCREMENT BY +1  
		    MINVALUE +1  
		    MAXVALUE +9223372036854775807  
		    NO CYCLE  
		    CACHE 20  
		    NO ORDER ),
    NAME               VARCHAR(255 BYTE) NOT NULL,
    DESCRIPTION        VARCHAR(4000 BYTE),
    STATUS             VARCHAR(32 BYTE) NOT NULL,
    ACTIVE_FROM        NUMERIC(19,0),
    ACTIVE_TO          NUMERIC(19,0),
    CREATED_DATE       NUMERIC(19,0),
    MODIFIED_DATE      NUMERIC(19,0),
    CONSTRAINT PDP_PLUGINS_PK PRIMARY KEY (ID)
);

CREATE TABLE PDP_PLUGIN_FILES (
    ID BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY (  
		    START WITH +1  
		    INCREMENT BY +1  
		    MINVALUE +1  
		    MAXVALUE +9223372036854775807  
		    NO CYCLE  
		    CACHE 20  
		    NO ORDER ),
    PLUGIN_ID          NUMERIC(19,0) NOT NULL,
    TYPE               VARCHAR(64 BYTE) NOT NULL,
    NAME               VARCHAR(260 BYTE) NOT NULL,
    CONTENT            BLOB(2147483647),
    MODIFIED_DATE      NUMERIC(19,0),
    CONSTRAINT PDP_PLUGIN_FILES_PK PRIMARY KEY (ID),
    CONSTRAINT PLUGIN_ID_FK FOREIGN KEY (PLUGIN_ID) REFERENCES PDP_PLUGINS
);

