INSERT INTO SYS_CONFIG (ID, APPLICATION, CONFIG_KEY, VALUE, DEFAULT_VALUE, VALUE_FORMAT, MAIN_GROUP, SUB_GROUP, MAIN_GROUP_ORDER, SUB_GROUP_ORDER, CONFIG_ORDER, HIDDEN, READ_ONLY, ADVANCED, UI, ENCRYPTED, RESTART_REQUIRED, DATA_TYPE, FIELD_TYPE, OPTIONS, REQUIRED, PATTERN, DESCRIPTION, MODIFIED_ON, MODIFIED_BY ) VALUES (NEXTVAL('HIBERNATE_SEQUENCE'), 'application', 'enrollmentservice.clientSecret', '', '', '{cipher}%s', 'security', 'enrollmentservice', '40', '100', '1', '1', '1', '1', '0', '1', '1', 'text', 'text', '', '1', '', 'Client secret for enrollment service.', CURRENT_TIMESTAMP, '-1' )
