DO $$
BEGIN
  ALTER TABLE DICT_ENROLLMENTS ADD description CHARACTER VARYING(4000)&semi
  ALTER TABLE DICT_ENROLLMENTS ADD issyncing BOOLEAN&semi
  ALTER TABLE DICT_ENROLLMENTS ADD created_by INT8&semi
  ALTER TABLE DICT_ENROLLMENTS ADD created_date TIMESTAMP&semi
  ALTER TABLE DICT_ENROLLMENTS ADD last_updated TIMESTAMP&semi
  ALTER TABLE DICT_ENROLLMENTS ADD last_updated_by INT8&semi
EXCEPTION
WHEN duplicate_column THEN NULL&semi
END&semi
$$ ;

UPDATE DICT_ENROLLMENTS SET issyncing = false;
UPDATE DICT_ENROLLMENTS SET created_by = -1;
UPDATE DICT_ENROLLMENTS SET created_date = CURRENT_TIMESTAMP;
UPDATE DICT_ENROLLMENTS SET last_updated_by = -1;
UPDATE DICT_ENROLLMENTS SET last_updated = CURRENT_TIMESTAMP;

ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN issyncing SET DEFAULT false;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN created_by SET DEFAULT -1;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN created_date SET DEFAULT CURRENT_TIMESTAMP;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN last_updated_by SET DEFAULT -1;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN last_updated SET DEFAULT CURRENT_TIMESTAMP;

ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN issyncing SET NOT NULL;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN created_by SET NOT NULL;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN created_date SET NOT NULL;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN last_updated_by SET NOT NULL;
ALTER TABLE DICT_ENROLLMENTS ALTER COLUMN last_updated SET NOT NULL;

