CREATE TABLE SYS_CONFIG (
    ID                 BIGINT NOT NULL,
    APPLICATION        CHARACTER VARYING(255) NOT NULL,
    CONFIG_KEY         CHARACTER VARYING(255) NOT NULL,
    VALUE              CHARACTER VARYING(4000),
    DEFAULT_VALUE      CHARACTER VARYING(4000),
    VALUE_FORMAT       CHARACTER VARYING(255),
    MAIN_GROUP         CHARACTER VARYING(255) DEFAULT 'default' NOT NULL,
    SUB_GROUP          CHARACTER VARYING(255) DEFAULT 'default' NOT NULL,
    MAIN_GROUP_ORDER   BIGINT DEFAULT 0 NOT NULL,
