    FIELD_TYPE         CHARACTER VARYING(20) DEFAULT 'text' NOT NULL,
    OPTIONS            CHARACTER VARYING(4000),
    REQUIRED           BOOLEAN DEFAULT '0' NOT NULL,
    PATTERN            CHARACTER VARYING(4000),
    DESCRIPTION        CHARACTER VARYING(4000),
    MODIFIED_ON        TIMESTAMP NOT NULL,
    MODIFIED_BY        BIGINT DEFAULT -1 NOT NULL,
    CONSTRAINT SYS_CONFIG_PK PRIMARY KEY ( ID ),
    CONSTRAINT SYS_CONFIG_UK1 UNIQUE ( APPLICATION, CONFIG_KEY )
);

